@@ -0,0 +1,67 @@
+import React, { useEffect } from 'react';
+import car from '../../assets/images/mapVector.svg'
+import repairPositions from './repairManPositions.js'
+//
+// {
+//    'method': 'get',
+//    'url': '/api/anonymous/service/218?latitude=48.9940649&longitude=2.5229373'
+//  }
+//
+
+export default function NewMap() {
+  useEffect(() => {
+    // Initialize the map
+    const mapOptions = {
+      center: { lat: 48.9940649, lng: 2.5229373 }, // Set the initial center of the map
+      zoom: 8, // Set the initial zoom level
+    };
+    const map = new window.google.maps.Map(document.getElementById('map'), mapOptions);
+
+    // Create markers
+
+
+
+    function addInfoWindow(marker, message) {
+
+      var infoWindow = new window.google.maps.InfoWindow({
+        content: message
+      });
+
+      window.google.maps.event.addListener(marker, 'click', function () {
+        infoWindow.open(map, marker);
+      });
+      marker.addListener('mouseover', function () {
+        // open your popup window
+        infoWindow.open(map, marker);
+      });
+
+      marker.addListener('mouseout', function () {
+        // close your popup window
+        infoWindow.close();
+      });
+    }
+    const carIcon = {
+      url: car,
+      scaledSize: new window.google.maps.Size(40, 40),
+    };
+
+
+    
+    repairManPositions.forEach((position) => {
+      const marker = new window.google.maps.Marker({
+        position,
+        map,
+        icon: carIcon,
+      });
+      addInfoWindow(marker, position['city_code'] + " " + position['department_name']);
+
+    });
+
+
+
+
+  }, []);
+
+  return <div id='map' style={{ width: '100%', height: '100vh' }}></div>;
+};
+

