+import React, { useEffect } from 'react';
+import { arrayRepairManPositions } from './repairManPositions.js'
+import { ReparateurIcon } from '../../assets/styles/icons.js';
+
+export default function NewMap() {
+  useEffect(() => {
+    // Initialize the map
+    const mapOptions = {
+      center: { lat: 48.9940649, lng: 2.5229373 }, // Set the initial center of the map
+      zoom: 8, // Set the initial zoom level
+    };
+    const map = new window.google.maps.Map(document.getElementById('map'), mapOptions);
+    // Create markers
+    function addInfoWindow(marker, message) {
+
+
+      
+      const infoWindow = new window.google.maps.InfoWindow({
+        content: message,
+        disableAutoPan: true,
+      });
+    
+      window.google.maps.event.addListener(marker, 'click', function () {
+        infoWindow.open(map, marker);
+      });
+      marker.addListener('mouseover', function () {
+        // open your popup window
+        infoWindow.setContent(label);
+        infoWindow.open(map, marker);
+      });
+      marker.addListener('mouseout', function () { infoWindow.close(); });
+    }
+    const repairIcon = {
+      url: ReparateurIcon,
+      scaledSize: new window.google.maps.Size(40, 40),
+    };
+
+
+
+    arrayRepairManPositions.forEach((position) => {
+      const marker = new window.google.maps.Marker({
+        position,
+        map,
+        icon: repairIcon,
+      });
+
+
+      const xrender = " <div style = color: 'blue';  fontSize: 7.77; fontFamily: 'Poppins';>" +
+        position['label'] + '</div>';
+      const render = "<b>" + position['label'] + '</b>' + " " + position['city_code'];
+      addInfoWindow(marker, xrender);
+
+    });
+
+
+
+
+  }, []);
+
+  return <div id='map' style={{ width: '100%', height: '100vh' }}></div>;
+};
+

