+import React, { useEffect } from 'react';
+import { RepairManlatlng } from './repairManPositions.js';
+import { ReparateurIcon } from '../../assets/styles/icons.js';
+import imgVector from "../../assets/images/icons/mapVector.svg";
+import { MarkerClusterer } from "@googlemaps/markerclusterer";
+export default function NewMap(positions, displayUniverseUsersIsReady) {
+
+
+  useEffect(() => {
+
+    function addInfoWindow(marker, message) {
+      var infoWindow = new window.google.maps.InfoWindow({
+        content: message
+      });
+      window.google.maps.event.addListener(marker, 'click', function () {
+        infoWindow.open(map, marker);
+      });
+      marker.addListener('mouseover', function () {
+        // open your popup window
+        infoWindow.open(map, marker);
+      });
+
+      marker.addListener('mouseout', function () {
+        // close your popup window
+        infoWindow.close();
+      });
+    }
+    const repairIcon = {
+      url: imgVector,
+      scaledSize: new window.google.maps.Size(40, 40),
+    };
+
+    // Initialize the map
+    const mapOptions = {
+      center: { lat: 48.9940649, lng: 2.5229373 }, // Set the initial center of the map
+      zoom: 5.8, // Set the initial zoom level
+    };
+    const map = new window.google.maps.Map(document.getElementById('map'), mapOptions);
+    console.log("STEP TU watch map positions", positions);
+    if (positions.position) {
+      let xi = 0;
+      var ent;
+
+      Object.entries(positions.position).forEach(entry => {
+        const [key, value] = entry;
+        let position = value;
+        ent = Object.entries(value);
+        if (position['lat'] && position['lng']) {
+          RepairManlatlng.push(position);
+        }
+ 
+      });
+      console.log("Step TU check position From Database ", RepairManlatlng);
+
+      const markers = RepairManlatlng.map((position, i) => {
+
+        const marker = new window.google.maps.Marker({
+          position,
+          map,
+          icon: repairIcon,
+        });
+        console.log("STEP TU Markers filling ",marker );
+        let renderx = "<b>" + position['label'] + "</b><br/>" + position['postalcode'] + "</b><br/>" + position['city_code']
+        addInfoWindow(marker, renderx);
+        xi++;
+        return marker;
+      });
+      var options = {
+        maxZoom: 100,
+
+        styles: [{
+          width: 53,
+          height: 53,
+          textColor: '#0002',
+        }]
+
+      };
+      new MarkerClusterer({ map, markers, algorithmOptions: { maxZoom: 10 } });
+
+    }
+
+  }, [displayUniverseUsersIsReady, positions]);
+
+  return <div id='map' style={{ width: '90%', height: '100vh' }}></div>;
+}
