+          const [key, value] = entry;
+          let position = value;
+          ent = Object.entries(value);
+          if (position['lat'] && position['lng']) {
+            RepairManlatlng.push(position);
+          }
+  
+        });
+        console.log("Step NM TU 0.3 watch map positions ", RepairManlatlng);
+        //setIsDisplayed(true);
+        const markers = RepairManlatlng.map((position, i) => {
+
+          const marker = new window.google.maps.Marker({
+            position,
+            map,
+            icon: repairIcon,
+          });
+        
+          let renderx = "<b>" + position['label'] + "</b><br/>" + position['postalcode'] + "</b><br/>" + position['city_code']
+          addInfoWindow(marker, renderx);
+          console.log("STEP TU Markers filling ", xi );
+          xi++;
+          return marker;
+        });
+        var options = {
+          maxZoom: 10,
+
+          styles: [{
+            width: 53,
+            height: 53,
+            textColor: '#0002',
+          }]
+
+        };
+        new MarkerClusterer({ map, markers, algorithmOptions: { maxZoom: 10 } });
+       
+      }
+ 
+  }, [positions, displayUniverseUsersIsReady]);
+
+  return <div id='map' style={{ width: '90%', height: '100vh' }}></div>;
