+
+
+  const showData = () => {
+    let data = RepairManlatlng;
+
+    if (false && !displayUniverseUsersIsReady) {
+      return <></>
+    }
+    else {
+      let xdata = [];
+      let xrow = 0;
+
+      if (positions.position) {
+        let xi = 0;
+        var ent;
+        let RepairManlatlng = [];
+        Object.entries(positions.position).forEach(entry => {
+          const [key, value] = entry;
+          let position = value;
+          ent = Object.entries(value);
+          // if (position['lat'] && position['lng']) {
+          xdata[xrow] = position;
+          xrow++;
+          // }
+
+        });
+      }
+      data = xdata;
+    }
+    if (data === false)
+      return (
+        <p className="loading-table" style={{ textAlign: "center" }}>
+          Chargement...
+        </p>
+      );
+    if (data.length === 0)
+      return (
+        <h3
+          style={{
+            textAlign: "center",
+            width: "100%",
+            fontSize: "22px",
+            fontWeight: "600",
+            margin: "40px 0",
+          }}
+        >
+          Aucune données
+        </h3>
+      );
+    return (
+      data.map(rowRenderArt)
+    )
+  };
+  const rowRenderArt = (row) => (
