+            <div style={{ left: 567, top: 444, position: 'absolute', color: 'white', fontSize: 20, fontFamily: 'Poppins', fontWeight: '600', wordWrap: 'break-word' }}>
+              Clients
+            </div>
+          
+            <div style={{ width: 524, height: 518, left: 470, borderRadius: 10 }} >
+
+              <Tabs
+
+                defaultActiveKey="clienttab"
+                justify
+
+                style={{ left: 470, width: 524, height: 466, top: 483, position: 'absolute' }}
+
+              >
+                <tab eventKey="clienttab" title="Client"
+                  style={{ left: 470, width: 524, height: 466, top: 520, position: 'absolute' }}
+                >
+  <div style={{ width: 262, padding: 10, left: 732, top: 434, position: 'absolute', background: '#CED5B2', borderTopLeftRadius: 4, borderTopRightRadius: 10, borderBottomRightRadius: 4, borderBottomLeftRadius: 4, justifyContent: 'center', alignItems: 'center', gap: 10, display: 'inline-flex' }}>
+              <div style={{ color: 'white', fontSize: 20, fontFamily: 'Poppins', fontWeight: '600', wordWrap: 'break-word' }}>
+                Réparateurs</div>
+            </div>
+
+                  Première fois sur Fingz?{" "}
+
+                  <Link
+                    to={"#"}
+                    onClick={(e) => {
+                      e.preventDefault();
+
+                      setDataModal({ typeModal: "client", title: "Espace client" });
+                      setLinkInscrir("client");
+                      setShowModal(true);
+                    }}
+                  >
+                    Inscrivez-vous
+                  </Link>
+
+                  <FormLogin onSubmit={submitLogin}>
+                    {message ? (
+                      <span className="error-form">{message}</span>
+                    ) : null}
+
+                    <Input
+                      {...state.email}
+                      onChange={(e) => {
