+
+                        setDataModal({ typeModal: "reparateur", title: "Espace Réparateur" });
+                        setLinkInscrir("reparateur");
+                        setShowModal(true);
+                      }}
+                      style={{ color: "#9ACA3C" }}
+                    >
+                      Inscrivez-vous
+                    </Link>
+
+                    <FormLogin onSubmit={submitLogin}>
+                      {message ? (
+                        <span className="error-form">{message}</span>
+                      ) : null}
+
+                      <Input
+                        {...state.email}
+                        onChange={(e) => {
+                          const cpState = { ...state };
+                          cpState.email.value = e.target.value;
+                          setState(cpState);
+                          setMessage(null);
+                        }}
+                      />
