+      name: "CONNEXION",
+      path: ROUTES.CHARTE_COOKIES.url,
+    },
+  ];
+
+  const [message, setMessage] = useState(null);
+  const [submitting, setSubmitting] = useState(false);
+
+  const [state, setState] = useState({
+    email: {
+      label: "Email",
+      name: "email",
+      placeholder: "Email",
+      value: "",
+      type: "email",
+      error: false,
+      errorMessage: "",
+      autocomplete: "username",
+      required: true,
+    },
+    password: {
+      label: "Mot de passe",
