+import React, { useEffect, useState } from "react";
+import ListProduits from "../../components/front/listProduits";
+import { useDispatch, useSelector } from "react-redux";
+
+import connector from "../../connector";
+import endPoints from "../../config/endPoints";
+import { useMediaQuery } from "react-responsive";
+import cordZ3img1 from '../../assets/images/z3-3.png';
+import cordZ3img2 from '../../assets/images/z3-2.png';
+import cordZ3img3 from '../../assets/images/z3-1.png';
+import cordZ3imgbottom from '../../assets/images/cordZ3imgbottom.png';
+import { useParams } from "react-router-dom";
+import MapArtisansUnivers from "./mapArtisansUnivers";
+import { Col, Container, Row } from "react-bootstrap";
+import Commentcamarche from "./ficheUniversParts/commentcamarche";
+import DecouvrezArtisan from "./ficheUniversParts/decouvrezArtisan";
+import AnatomieUniverse from "./ficheUniversParts/anatomieUniverse";
+import PrestationsFavorites from "./ficheUniversParts/prestationsFavorites";
+import anatomieCord from '../../assets/images/zanatCord.png';
+import ButtonDef from "../../components/ui-elements/buttonDef";
+
+
+function strUcFirst(a) {
+  return (a + '').charAt(0).toUpperCase() + (a + '').substr(1);
+}
+export default function FicheUnivers(props
+) {
+  const { isDisplayListeProduits, setDisplayListeProduits } = props;
+  const { isDisplayFicheUnivers, setDisplayFicheUnivers } = props;
+
+  const [service, setService] = useState(false);
+  const [benefits, setBenefits] = useState([]);
+  const [activeUniverse, setActiveUniverse] = useState(false);
+  const universe = useSelector((state) => state.universe);
+  const [placeIsSelected, setPlaceIsSelected] = useState(false);
+  const [isPending, setIsPending] = useState(false);
+  const [isPendingLatLng, setIsPendingLatLng] = useState(true);
+  const [paramsUrlService, setParamsUrlService] = useState("");
+  const isTabletOrMobile = useMediaQuery({ query: "(max-width: 993px)" });
+  const { slugIdUniverse, slugIdService } = useParams();
+  const [universeTitle, setuniverseTitle] = useState("");
+  const [universeDescription, setuniverseDescription] = useState('');
+  const getUniverse = () => {
+    const id = slugIdUniverse.split("-").pop();
+    if (id) {
+      connector({
+        method: "get",
+        url: `${endPoints.ANONYMOUS_UNIVERSE}${id}/categories`,
+        success: (response) => {
+          const arrayObj = response.data || { categories: [] };
+console.log("ANONYMOUS_UNIVERSE", arrayObj);
+          if (response.data?.name) {
+            setuniverseTitle(response.data.name);
+            setuniverseDescription(response.data.description);
+            if (response.data.name === 'Cordonnerie') {
+              setuniverseDescription("Nos artisans partenaires ne manquent ni de talons, ni de talents. Parfaitement bien dans leurs baskets, ils redonnent forme aux escarpins, bottes, ballerines et sneakers qui passent entre leurs mains. Ils sont du genre à bichonner vos chaussures de la semelle jusqu’au dernier lacet : pas question de s’arrêter au premier coup de pompe !");
+            }
+            //console.log('details Univers', response.data);
+          }
+        },
+        catch: (error) => {
+          console.log(error);
+        },
+      });
+    }
+  };
+  const handleClick = () => {
+    // 👇 "message" stores input field value
+    setDisplayListeProduits(true);
+  };
+  useEffect(() => {
+    getUniverse();
+  }, []);
+
+  const handleProductListClick = () => {
+    // 👇 "message" stores input field value
+
+
+    setDisplayListeProduits(!isDisplayListeProduits);
+    setDisplayFicheUnivers(!isDisplayFicheUnivers);
+  };
+
+  return <Container>
+
+    <div style={{ width: '100%', height: '100%', flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 24, display: 'inline-flex' }}>
+      <div style={{
+        width: 623, height: 95, color: '#4D5F68', fontSize: 96,
+        fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word'
+      }}>
+        {universeTitle}</div>
+      <div style={{ width: 1000, color: '#929292', fontSize: 20, fontFamily: 'Helvetica Neue LT Std', fontWeight: '500', lineHeight: 1, wordWrap: 'break-word' }}>
+        {universeDescription}</div>
+    </div>
+
+    
+    <div>
+      <div style={{ width: '50%', height: '100%', marginLeft: "25%",  marginTop: 20, marginBottom: 20, background: '#9ACA3C', borderRadius: 16, justifyContent: 'center', alignItems: 'center', gap: 10, display: 'inline-flex' }}>
+        <div style={{ color: 'white', fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word' }}>
+          <ButtonDef width="50%" background='#9ACA3C' textButton={"Choisissez votre prestation"} onClick={handleProductListClick}></ButtonDef></div>
+      </div>
+    </div>
+    <Container>
+      <Row>
+        <Col class="col-lg-3">
+          <img style={{ width: '100%' }} src={cordZ3img1} /></Col>
+        <Col class="col-lg-3">
+          <img style={{ width: '100%' }} src={cordZ3img2} /></Col>
+        <Col class="col-lg-3">
+          <img style={{ width: '100%' }} src={cordZ3img3} /></Col>
+      </Row>
+      <Row>
+        <Col>
+          <img className="z3imgbottom" style={{ width: '100%' }} src={cordZ3imgbottom} />
+        </Col>
+      </Row>
+
+    </Container>
+
+    <Commentcamarche></Commentcamarche>
+    <MapArtisansUnivers></MapArtisansUnivers>
+  
+    <AnatomieUniverse
+      title={universeTitle}
+      image={anatomieCord}
+    ></AnatomieUniverse>
+    <PrestationsFavorites></PrestationsFavorites>
+
+  </Container>
+}

