+) {
+  const { isDisplayListeProduits, setDisplayListeProduits } = props;
+  const { isDisplayFicheUnivers, setDisplayFicheUnivers } = props;
+
+  const [service, setService] = useState(false);
+  const [benefits, setBenefits] = useState([]);
