import React from "react";
import { useSelector } from "react-redux";
import { Col, Container, Row } from "react-bootstrap";
//import BannerHome from "../components/front/bannerHome";
import BannerHomeNc from "../components/front/bannerHomeNc";
import Base from "../theme/front/base";
import iconParcours from "../assets/images/icons-services-home/homepage-fluxparcours.svg";
import iconPourquoichoisir from "../assets/images/icons-services-home/homepage-pourquoichoisir.svg";
import iconPchp1 from "../assets/images/z8iconos_home_page_051.svg";
import iconPchp2 from "../assets/images/z8iconos_home_page_071.svg";
import iconPchp3 from "../assets/images/z8plandetravail14x1.svg";
import iconPchp4 from "../assets/images/z8iconos_home_page_061.svg";
import {
  ContentPageStyle,
  HomeReparateur,
  HomeBlocs,
} from "../assets/styles/frontGlobalStyle";
import Partenaires from "./universeStep/Partenaires";

import { Link } from "react-router-dom";
import slideImage1 from "../assets/images/slider-image/home-slider-1.png";
import slideImage2 from "../assets/images/slider-image/home-slider-2.png";
import slideImage3 from "../assets/images/slider-image/home-slider-3.png";
import slideImage4 from "../assets/images/slider-image/home-slider-4.png";
import slideImage5 from "../assets/images/slider-image/home-slider-5.png";
import slideImage6 from "../assets/images/slider-image/home-slider-6.png";
import noImage from "../assets/images/noImage.png";
import * as vars from "../vars";
import Commentcamarche from "./front/tunnel/universeStep/commentcamarche";
import ParcoursClient from "./front/parcoursclient";
import Partenaires from "./front/tunnel/universeStep/Partenaires";

export default function Home() {
  const getPathImage = (image) => (image ? vars.pathImage + image : noImage);
  const universe = useSelector((state) => state.universe);
  const isDisplayedBottom = false;
  const sliderImages = [
    {
      id: 1,
      url: slideImage1,
    },
    {
      id: 2,
      url: slideImage2,
    },
    {
      id: 3,
      url: slideImage3,
    },
    {
      id: 4,
      url: slideImage4,
    },
    {
      id: 5,
      url: slideImage5,
    },
    {
      id: 6,
      url: slideImage6,
    },
  ];
  return (
    <Base>
      <BannerHomeNc
        sliderBanner={sliderImages}
        title="Faites réparer les objets qui vous sont chers"
        isDisplayedBottom={isDisplayedBottom}
      />

      <ContentPageStyle>
        <Container>
          <HomeReparateur>

            <Row>
              <ParcoursClient>

              </ParcoursClient>
            </Row>

          </HomeReparateur>
          <HomeBlocs>
            <div style={{ textAlign: 'center', color: '#465A61', fontSize: 40, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 2, wordWrap: 'break-word' }}>Découvrez nos services de réparation</div>
            <Row>
              {universe.allUniverses &&
                universe.allUniverses.map((row) => (
                  <Col lg={4} sm={6} className="item-cat-univers" key={row.id}>
                    <Link
                      to={`/univers/${row.slug}-${row.id}`}
                      className="content-item-univers"
                    >
                      <img src={getPathImage(row.imageHome)} alt={row.name} />
                      <p className="name-cat-univers">{row.name}</p>
                    </Link>
                  </Col>
                ))}
            </Row>
          </HomeBlocs>
          <HomeReparateur>
            <div style={{ textAlign: 'center', color: '#465A61', fontSize: 40, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 2, wordWrap: 'break-word' }}>Pourquoi choisir Fingz ?</div>
            <Row>

              <div style={{ marginRight: 8, marginBottom: 8, width: 600, height: 160, paddingLeft: 8, paddingRight: 8, paddingTop: 20, paddingBottom: 20, background: '#EDEFF0', boxShadow: '0px 2px 4px rgba(70, 90, 97, 0.15)', borderRadius: 16, justifyContent: 'flex-start', alignItems: 'center', gap: 16, display: 'inline-flex' }}>
                <img style={{ width: 125, height: 124.76 }} src={iconPchp1} />
                <div style={{ height: 125, paddingTop: 10, paddingBottom: 10, flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 12, display: 'inline-flex' }}>
                  <div style={{ width: 360, color: '#5D5D5D', fontSize: 18, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word' }}>Economique</div>
                  <div style={{ width: 420, color: '#5D5D5D', fontSize: 16, fontFamily: 'Helvetica Neue LT Std', fontWeight: '400', lineHeight: 1, wordWrap: 'break-word' }}>En prolongeant la durée de vie de vos objets, vous faites des économies.</div>
                </div>
              </div>
              <div style={{ marginRight: 8, marginBottom: 8, width: 600, height: 160, paddingLeft: 8, paddingRight: 8, paddingTop: 20, paddingBottom: 20, background: '#F6F9E8', boxShadow: '0px 2px 4px rgba(70, 90, 97, 0.15)', borderRadius: 16, justifyContent: 'flex-start', alignItems: 'center', gap: 16, display: 'inline-flex' }}>
                <img style={{ width: 125.24, height: 125 }} src={iconPchp4} />
                <div style={{ height: 125, paddingTop: 10, paddingBottom: 10, flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 12, display: 'inline-flex' }}>
                  <div style={{ width: 360, color: '#5D5D5D', fontSize: 18, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word' }}>Ecologique</div>
                  <div style={{ width: 420, color: '#5D5D5D', fontSize: 16, fontFamily: 'Helvetica Neue LT Std', fontWeight: '400', lineHeight: 1, wordWrap: 'break-word' }}>Devenez acteur de l’économie circulaire en donnant une 2ème vie à vos objets et préservez les ressources de la planète.</div>
                </div>
              </div>
              <div style={{ marginRight: 8, marginBottom: 8, width: 600, height: 160, paddingLeft: 8, paddingRight: 8, paddingTop: 20, paddingBottom: 20, background: '#EAF9FF', boxShadow: '0px 2px 4px rgba(70, 90, 97, 0.15)', borderRadius: 16, justifyContent: 'flex-start', alignItems: 'center', gap: 16, display: 'inline-flex' }}>
                <img style={{ width: 125, height: 125 }} src={iconPchp3} />
                <div style={{ height: 125, paddingTop: 10, paddingBottom: 10, flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 12, display: 'inline-flex' }}>
                  <div style={{ width: 360, color: '#5D5D5D', fontSize: 18, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word' }}>Fiable</div>
                  <div style={{ width: 420, color: '#5D5D5D', fontSize: 16, fontFamily: 'Helvetica Neue LT Std', fontWeight: '400', lineHeight: 1, wordWrap: 'break-word' }}>Les artisans partenaires sont des professionnels qui s’engagent à vous fournir un service de qualité et à faire de la réparation leur priorité.</div>
                </div>
              </div>
              <div style={{ marginRight: 8, marginBottom: 8, width: 600, height: 160, paddingLeft: 8, paddingRight: 8, paddingTop: 20, paddingBottom: 20, background: '#FCF2EA', boxShadow: '0px 2px 4px rgba(70, 90, 97, 0.15)', borderRadius: 16, justifyContent: 'flex-start', alignItems: 'center', gap: 16, display: 'inline-flex' }}>
                <img style={{ width: 125, height: 125 }} src={iconPchp2} />
                <div style={{ height: 125, paddingTop: 10, paddingBottom: 10, flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 12, display: 'inline-flex' }}>
                  <div style={{ width: 360, color: '#5D5D5D', fontSize: 18, fontFamily: 'Helvetica Neue LT Std', fontWeight: '700', lineHeight: 1, wordWrap: 'break-word' }}>Local</div>
                  <div style={{ width: 420, color: '#5D5D5D', fontSize: 16, fontFamily: 'Helvetica Neue LT Std', fontWeight: '400', lineHeight: 1, wordWrap: 'break-word' }}>En choisissant des réparateurs près de chez vous, vous soutenez l’artisanat de proximité et limitez l’impact CO2 lié au transport.</div>

                </div>
              </div>
            </Row>
          </HomeReparateur>
          <Partenaires>   </Partenaires>

        </Container>
      </ContentPageStyle>
      <BannerHomeNc
        sliderBanner={sliderImages}
        title="Faites réparer les objets qui vous sont chers"
        isDisplayedBottom={isDisplayedBottom}
      />
    </Base>
  );
}
